INSERT INTO information VALUES(1289,0,7,'Monthly Karma Pack On Sale',1692324000000,replace('<section>\n <div class="info-largeimage">\n <img src="/images/en/operations/banner/5dfaf5aa90c9175711ad537eca6e61036ff4b38868220995d014df817c679056">\n </div>\n <p>\n Hello, and thank you for playing NieR Re[in]carnation. <br />\n This is a message from the management team.<br />\n <br />\n The <span class="text-red">Monthly Karma Packs</span> are now available for purchase as of Aug. 17 18:00 PST.<br />\n \n </p>\n </section>\n \n  <br />\n\n <section>\n \n <div class="information_table">\n  <table>\n  <thead>\n  <tr>\n  <th>Monthly Karma Pack (Special)</th>\n  </tr>\n  </thead>\n  <thead>\n  <tr>\n  <td>\n Product Details &amp; Bonus Content<br>\n Paid Gems x3,060,  Karma Stone x30<br>\n Purchasable Amount<br>\n 1</td>\n  </tr>\n  </thead>\n  </table>\n </div>\n\n <div class="information_table">\n  <table>\n  <thead>\n  <tr>\n  <th>Monthly Karma Pack (Economic)</th>\n  </tr>\n  </thead>\n  <thead>\n  <tr>\n  <td>\n Product Details &amp; Bonus Content<br>\n Paid Gems x6,100, Karma Stone x30<br>\n Purchasable Amount<br>\n 1</td>\n  </tr>\n  </thead>\n  </table>\n </div> \n \n <div class="information_table">\n  <table>\n  <thead>\n  <tr>\n  <th>Monthly Karma Pack</th>\n  </tr>\n  </thead>\n  <thead>\n  <tr>\n  <td>\n Product Details &amp; Bonus Content<br>\n Paid Gems x10,000,  Karma Stone x30<br>\n Purchasable Amount<br>\n 1</td>\n  </tr>\n  </thead>\n  </table>\n </div>\n\n </section>\n \n\n \n <section>\n  <h3>\n  Notes\n  </h3>\n  <div class="attention_content">\n  <ul>\n  <li>\n  Event duration and content are subject to change without notice.\n  </li>\n  <li>\n  Purchases will not be sent to your gift box, but will be added directly to your inventory.\n  </li>\n  <li>\n  This campaign and its content may reappear in the future.\n  </li>\n  </ul>\n  </div>\n <br />\n </section>\n \n <section>\n <p>\n Thank you very much for your ongoing support for NieR Re[in]carnation.\n </p>\n </section>','\n',char(10)),NULL,NULL,'/images/en/operations/banner/d82ba0d5121f481356bf3fd44ea69918d965160f40449e38c16b828715408946');
